<#

DESCRIPTION

This script would scan all active directory users and send an email to any user whose password was about to expuire with instructions on how to reset their password

#>

$attachstring = ''
import-module activedirectory
If (!(Test-path "$env:USERPROFILE\Pictures\Acuitylogo.png"))
{
    [IO.File]::WriteAllBytes("$env:USERPROFILE\Pictures\Acuitylogo.png", [Convert]::FromBase64String($attachstring))
}
$CurrentDate = Get-Date

#smtp server settings
$smtpServer = "smtpmail.acuitybrands.com"
$smtp = New-Object Net.Mail.SmtpClient($smtpServer)
$smtp.Port = 25
$att = New-Object System.Net.Mail.Attachment("$env:USERPROFILE\Pictures\Acuitylogo.png")
$att.ContentDisposition.Inline = $True
$att.ContentDisposition.DispositionType = "Inline"
$att.ContentType.MediaType = "image/png"
$att.ContentId = "att"
#Email Message settings
$Email = new-object Net.Mail.MailMessage
$Email.From = "`"Acuity Password Expiration Notification`" <passwordexpire@acuitybrands.com>"
$Email.Subject = "Password expiration notification"
$Email.IsBodyHtml = $true
$Email.Attachments.Add($att)

$Users = Get-ADUser -filter {Enabled -eq $True -and PasswordNeverExpires -eq $False} ?Properties "GivenName","sn", "DisplayName", "mail", "passwordlastset" , "msDS-UserPasswordExpiryTimeComputed" | Select-Object -Property "sn","GivenName","Mail","Displayname", "passwordlastset",@{Name="ExpiryDate";Expression={[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed")}}
Foreach ($user in $users)
{
$displayname = $user.Displayname
if($User.ExpiryDate)
    {           
        #Calculate the time that is left before the password expires
        $PasswordExpireDate = [DateTime]$User.ExpiryDate
 
        $TimeLeftUntilExpire = (NEW-TIMESPAN ?Start $CurrentDate ?End $PasswordExpireDate).Days
  
       $TimeLeftUntilExpire
$body = @"
<html>
<body lang=EN-US link=blue vlink=purple style='tab-interval:.5in'>

<div class=WordSection1>
<img src="cid:att">
<p class=MsoNormal><b style='mso-bidi-font-weight:normal'>${displayname},<o:p></o:p></b></p>

<p class=MsoNormal><o:p>Your network account password will expire in ${TimeLeftUntilExpire} day(s). Follow the instructions below based on your location before this time. If your password expires, a support ticket will need to be entered and approved by your manager before your password will be reset.</o:p></p>

<p class=MsoNormal>If you are working remotely:</p>

<ol type="1">
	<li>Connect to VPN</li>
	<li>Press Ctrl+Alt+Delete on your keyboard</li>
	<li>Select option to change your password</li>
	<li>Enter your old and new passwords</li>
	<li>Press Enter to change password</li>
	<li>Press Ctrl+Alt+Delete and select Lock</li>
	<li>Unlock the computer with your new password</li>
	<ol type="a">
		<li>This will cache your password locally to the machine so you will only have to use one password.</li>
	</ol>
</ol>

	

<p class=MsoNormal>If you are working in an Acuity office:</p>

<ol type="1">
	<li>Press Ctrl+Alt+Delete on your keyboard</li>
	<li>Select option to change your password</li>
	<li>Enter your old and new passwords</li>
	<li>Press Enter to change password</li>
</ol>


<p class=MsoNormal>Thank you for your cooperation.</p>

</div>

</body>

</html>
"@
If (($TimeLeftUntilExpire -eq 10) -or ($TimeLeftUntilExpire -eq 5) -or ($TimeLeftUntilExpire -eq 1))
{
    $Email.Body = $body
    $Email.To.Clear()
    $Email.To.Add($user.Mail)
    $smtp.Send($Email)
		Write-host "Sent email to $displayname, who has $TimeLeftUntilExpire day(s) until password expires."
}
}
}
